mapper.select:
select from it_instead
AND (old_staff like '%${staff}%'
OR instead_staff like '%${staff}%')
AND instead_shift like '%${insteadShift}%'
AND instead_date <= #{endDate}
AND instead_date >= #{beginDate}

  select 
  from it_instead
  	
  		old_staff = #{staff,jdbcType=VARCHAR} 
		
			AND instead_shift like '%${insteadShift}%'
		
		
			AND instead_date <= #{endDate} 
		
		
			AND instead_date >= #{beginDate}

mapper.insert:
  
    select  replace(uuid(),'-','')   from dual  

insert into it_instead (id, old_staff, instead_staff, 
  instead_shift, instead_date, instead_reason
  )
values (#{id,jdbcType=VARCHAR}, #{oldStaff,jdbcType=VARCHAR}, #{insteadStaff,jdbcType=VARCHAR}, 
  #{insteadShift,jdbcType=VARCHAR}, #{insteadDate,jdbcType=TIMESTAMP}, #{insteadReason,jdbcType=VARCHAR}
  )
