mapper.select:
  select 
  from it_logbook
  	
		
			AND staff like '%${staff}%'
		
		
			AND shift like '%${shift}%'
		
		
			AND create_time <= #{endDate} 
		
		
			AND create_time >= #{beginDate}

  select 
  from it_logbook
  	
		staff = #{staff,jdbcType=VARCHAR}
		
			AND shift like '%${shift}%'
		
		
			AND create_time <= #{endDate} 
		
		
			AND create_time >= #{beginDate}
